import cities from "../json/cities.json";
function Dropdown() {
  return (
    <div>
      ;
      <button
        className="btn btn-secondary dropdown-toggle"
        type="button"
        data-bs-toggle="dropdown"
        aria-expanded="false"
      >
        Cities
      </button>
      <div className="dropdown-menu">
        {cities.map((item) => (
          <div key={item.id}>
            <span className="dropdown-item" onClick={() => {}}></span>
          </div>
        ))}
      </div>
    </div>
  );
}

export default Dropdown;
